package com.core;

public class ControlStatements3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3000;
		int b=56;
		int c=340;
		if((a>b)&&(a>c))
		{
		System.out.println("A is big");
		}
		else if((b>a)&&(b>c))
		{
		System.out.println("B is big");
		}
		else
		{
		System.out.println("C is big");
		}
	}

}
